package fjab

import scala.collection.mutable


object Workshop extends App {

  def pylons(k: Int, arr: Array[Int]): Int = {
    val cache = mutable.Map[List[Int],Int]()
    val maxCoverage = (k-1)*2+1

    def loop(list: List[Int]): Int = {
      val value = cache.get(list)
      if (value.isDefined) value.get
      else if (!list.contains(1)) Integer.MAX_VALUE
      //else if (list.length <= maxCoverage) 1
      else {
        val debug = Range(0, k).filter(i => list(maxCoverage - i - k) == 1).toList
        val result = Option(Range(0, k)
          .filter(i => list(maxCoverage - i - k) == 1)
          .map { i =>
            val r = loop(list.drop(maxCoverage - i - k + 1))
            if (r == Integer.MAX_VALUE) r else r + 1
          })
          .filterNot(_.isEmpty)
          .map(_.min)
          .getOrElse(Integer.MAX_VALUE)

        cache += (list -> result)
        result
      }
    }

    val numPlants = loop(arr.toList)
    if(numPlants == Integer.MAX_VALUE) -1 else numPlants

  }

